require(['jquery'], function($){
    /* Get src attribute from img */
    $(".bg-img").each(function() {
        var urlImg = $(this).attr('src'),
        parent = $(this).parent();
        $(this).remove();
        parent.css(
        {
        'background':'url("'+urlImg+'") no-repeat 50% 0',
        'background-size': 'cover'
        });
    });
});
